const mascotSVGs = {
  happy: mascot1SVG,
  excited: mascot1SVG,
  satisfied: mascot1SVG,
  default: mascot1SVG,
  sad: mascot2SVG,
  hungry: mascot2SVG,
};

export function Mascot({ emotion = 'default', size = 100, style }: MascotProps) {
  const svgContent = mascotSVGs[emotion] || mascotSVGs.default;
  
  return (
    <View style={[styles.container, style]}>
      <SvgXml 
        xml={svgContent} 
        width={size} 
        height={size * (265/291)} // Maintain aspect ratio
      />
    </View>
  );
}

